import logo from "./logo.svg";
import "./App.css";
import { Component } from "react";

const Mix = ({name, url}) => (
  <div>
    <h2>{name}</h2>
    <p>{url}</p>
  </div>
)

class App extends Component {
  constructor(props) {
    super(props);
    this.state = {
      mixes: []
    }
  }

  componentDidMount() {
    //this.searchMixCloud("ntsradio")
    this.stationRoundUp()
  }

  searchMixCloud = async (mixSearchTerm) => {
    try {
      const response = await fetch(`https://api.mixcloud.com/${mixSearchTerm}/cloudcasts/`)
      const { data } = await response.json();
      // this.mixLists(data)
      // const response = await fetch('https://api.are.na/v2/channels/test-channel-kecfu5msrfs')
      // const { contents} = await response.json();
      // console.log(data);

      this.mixLists(data)
    } catch(error) {
      console.log(error);
    }
  }


  stationRoundUp = () => {
    const radioStations = [
      "balamii",
      "CashmereRadio",
      "dazed",
      "hotelradioparis",
    ]

    radioStations.map((station) => {
      this.searchMixCloud("ntsradio")
    })
  }

  mixLists = (mix) => {
    mix.map((el) => {
      const {name, url} = el
      this.setState((prevState, props) => ({
        ...prevState,
        mixes: [...prevState.mixes, { name: name, url: url}]
      }))
    })
  }



  render() {
    const {mixes} = this.state

    return (
      <div className="App" onLoad={this.searchMixes}>
        <h1>Hi</h1>

        {mixes.map(mix => (
          <h2>
            <a href={mix.url}>
              {mix.name}
            </a>
          </h2>
        ))}
      </div>
    );
  }
}

export default App;
